// for formats like d/M/y (european format) before M/d/y (American).
// Returns a Date object or null if no patterns match.
// ------------------------------------------------------------------
function parseDate(val) {
    var preferEuro=(arguments.length==2)?arguments[1]:false;
    generalFormats=new Array('y-M-d','MMM d, y','MMM d,y','y-MMM-d','d-MMM-y','MMM d');
    monthFirst=new Array('M/d/y','M-d-y','M.d.y','MMM-d','M/d','M-d');
    dateFirst =new Array('d/M/y','d-M-y','d.M.y','d-MMM','d/M','d-M');
    var checkList=new Array('generalFormats',preferEuro?'dateFirst':'monthFirst',preferEuro?'monthFirst':'dateFirst');
    var d=null;
    for (var i=0; i<checkList.length; i++) {
        var l=window[checkList[i]];
        for (var j=0; j<l.length; j++) {
            d=getDateFromFormat(val,l[j]);
            if (d!=0) {return new Date(d);}
        }
    }
    return null;
}


function ieInnerHTML(html, convertToLowerCase) {
    var zz = html;
    var z = zz.match(/<\/?\w+((\s+\w+(\s*=\s*(?:".*?"|'.*?'|[^'">\s]+))?)+\s*|\s*)\/?>/g);

    if (z) {
        for (var i = 0; i < z.length; i++) {
            var y;
            var zSaved = z[i];
            var attrRE = /\=[a-zA-Z\{\}\|\.\:\[\]_\(\)\&\$\%#\@\!0-9]+[?\s+|?>]/g;

            z[i] = z[i].replace(/(<?\w+)|(<\/?\w+)\s/, function(a){ return a.toLowerCase(); });
            y = z[i].match(attrRE);//deze match

            if (y) {
                var j = 0;
                var len = y.length;

                while (j < len) {
                    var replaceRE = /(\=)([a-zA-Z\{\}\|\.\:\[\]_\(\)\&\$\%#\@\!0-9]+)?([\s+|?>])/g;
                    var replacer = function() {
                      var args = Array.prototype.slice.call(arguments);
                      return '="'+(convertToLowerCase ? args[2].toLowerCase() : args[2])+'"'+args[3];
                    };

                    z[i] = z[i].replace(y[j], y[j].replace(replaceRE,replacer));

                    j++;
                }
            }
            zz = zz.replace(zSaved,z[i]);
        }
    }
    return zz;
}

